import React, { useEffect, useState } from "react";
import {NavLink} from 'react-router-dom';
import axios from "axios";

const Products = () => {
  const [productsData, setProductsData] = useState([]);
  const [filterProduct, setFilterProduct] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    axios
      .get("/api/auth/products")
      .then((response) => {
        setProductsData(response.data);
        setFilterProduct(response.data);
        setLoading(false);
        console.log(response.data);
      })
      .catch((err) => {
        console.log(err);
        setLoading(true);
      });
  }, []);

  const filterProductData = (cat) => {

  }

  const ShowProducts = () => {
    return (
        <>
            <div className="buttons d-flex justify-content-center mb-5 pb-5">
                <button className="btn btn-outline-dark me-2"
                        onClick={() => FilterProduct(productsData)}>All
                </button>
                <button className="btn btn-outline-secondary me-2"
                        onClick={() => FilterProduct("coffee")}>
                    Coffee
                </button>
                <button className="btn btn-outline-secondary me-2"
                        onClick={() => FilterProduct("tea")}>
                    Tea
                </button>
                <button className="btn btn-outline-secondary me-2"
                        onClick={() => filterProduct("frappuccino")}>
                    Frappuccino
                </button>
            </div>
            {productsData.map(product => {
                return (
                    <>
                        <div className="col-12 col-md-6 col-lg-4 mt-3" key={product.id}>
                            <div className="card h-100 text-center p-4" key={product.id}>
                                <img
                                    src={product.imgUrl}
                                    className="card-img-top"
                                    alt={product.description}
                                    height="250px"
                                />
                                <div className="card-body">
                                    <p className="card-text lead fw-bold mt-2">{product.name}</p>
                                    <p className="card-text lead ">${product.price}</p>
                                    <NavLink to={`/products/${product.id}`} className="btn btn-outline-dark">
                                        Buy Now
                                    </NavLink>
                                </div>
                            </div>
                        </div>
                    </>
                );
            })}
        </>
    );
};

  return (
    <div>
      <div className="container my-5 py-5">
        <div className="row">
          <div className="col-12 mb-5">
            <h1
              className="display-6 fw-bolder
            text-center"
            >
              Latest Products
            </h1>
            <hr />
          </div>
        </div>
        <div className="row justify-content-center">
          {loading ? <h1>Loading</h1> : <ShowProducts/>}
        </div>
      </div>
    </div>
  );
};

export default Products;
