import axios from "axios";
import React, { useState, useEffect } from "react";
import { useParams } from "react-router-dom";

const OrderProduct = () => {
  const [orderData, setOrderData] = useState([]);
  const [orderedProduct, setOrderedProduct] = useState([
    {
        "id": 2,
        "name": "Tea",
        "price": 2.22,
        "quantity": 1
    },
    {
        "id": 3,
        "name": "Frappuccino",
        "price": 3.5,
        "quantity": 1
    }
]);
  const { id } = useParams();

  useEffect(() => {
    console.log("Hello World");
    getOrderedProduct();
  }, []);

  const getOrderedProduct = () => {
    axios.get(`/order/get/${id}`).then((response) => {
      setOrderData(response.data);
      console.log(response.data.orderedProducts);
      setOrderedProduct(response.data.orderedProducts);
      console.log(response.data);
    });
  };

  const ShowOrderedProduct = () => {
    return (
      <div className="text-center">
        <div className="card">
          <div className="card-body">
            {
                Object.keys(orderedProduct).map(function(product) {
                    return <h5 className="card-title">{product.name}</h5>;
                } )
            }
            {
                orderedProduct.map((product)=>{
                    <h1>{product.id}</h1>
                })
            }
            <h6 className="card-subtitle mb-2 text-muted">{orderData.id}</h6>
            <h6 className="card-subtitle mb-2 text-muted">
              {orderData.boughtAt}
            </h6>
            <p className="card-text"></p>
          </div>
        </div>
      </div>
    );
  };

  return <ShowOrderedProduct />;
};

export default OrderProduct;
