import axios from "axios";
import React, { useState, useEffect } from "react";
import axiosService from "../service/AxiosService";
import { useLocalState } from "../util/useLocalStroge";

const Cart = () => {

  const [jwt, setJwt] = useLocalState("", "jwt");
  const [userData, setUserData] = useState([]);
  const [loading, setLoading] = useState(true);
  const [cart, setCart] = useState([]);

  useEffect(() =>{
      axiosService("/cart", jwt)
    .then(response =>{
      setUserData(response.data);
      setCart(response.data.cart.products)
      console.log(response.data.cart.products)
      console.log(response.data);
      setLoading(false);
    })
    .catch(err =>{
    })
  }, [])

  const Loading = () => {
    return <h1>Loading...</h1>;
  };

  const Cart = () =>{
    return(
      <>
        <div className="container">

        </div>
      </>
    )
  }

  const ShowCart = () => {
    return (
      <>
          {cart ? <Cart />:
           <div className="container mt-5">There is nothing in this cart</div>}
      </>
    );
  };

  return <div className="container">{loading ? <Loading /> : <ShowCart />}</div>;
};

export default Cart;
