import axios from "axios";
import React, { useState, useEffect } from "react";
import { useParams } from "react-router-dom";
import Products from "./Products";

const OrderProduct = () => {
  const [orderData, setOrderData] = useState([]);
  const [userData, setUserData] = useState([]);
  const [orderedProduct, setOrderedProduct] = useState([]);
  const { id } = useParams();

  useEffect(() => {
    getOrderedProduct();
  }, []);

  const getOrderedProduct = () => {
    axios.get(`/order/get/${id}`).then((response) => {
      setOrderData(response.data);
      //   console.log(response.data.orderedProducts);
      setUserData(response.data.user);
      setOrderedProduct(response.data.orderedProducts);
      console.log(response.data);
    });
  };

  const ShowOrderedProduct = () => {
    return (
      <div className="text-center">
        <div className="card">
          <div className="card-body">
            <h6 className="card-subtitle mb-2 text-muted">{userData.username}</h6>
            <h6 className="card-subtitle mb-2 text-muted">
              {orderData.boughtAt}
            </h6>
            <p className="card-text"></p>
            {orderedProduct.map((product) => {
              return (
                <div className="container" key={product.id}>
                  <h1>{product.name} Ã— {product.quantity} </h1>
                </div>
              );
            })}
          </div>
        </div>
      </div>
    );
  };

  return <ShowOrderedProduct />;
};

export default OrderProduct;
