import React, { useState, useEffect } from "react";
import axios from "axios";
import { Link } from "react-router-dom";
import { Col, Row } from "react-bootstrap";
import axiosService from "../service/AxiosService";

const User = () => {
  const [orders, setOrders] = useState([]);
  const [filterUsers, setFilterUsers] = useState(orders);
  const [loading, setLoading] = useState(true);
  const [searchInput, setSearchInput] = useState("");

  useEffect(() => {
    axiosService("/order/list")
      .then((response) => {
        setOrders(response.data);
        console.log(response.data);
        setLoading(false);
      })
      .catch((err) => {});
  }, []);

  const Loading = () => {
    return (
      <>
        <h1>This page is only available for admin</h1>
      </>
    );
  };

  const ShowUsers = () => {
    return (
      <div className="container">
        <table className="table">
          <thead>
            <tr>
              <th scope="col">ID</th>
              <th scope="col">First Name</th>
              <th scope="col">Last Name</th>
              <th scope="col">Username</th>
            </tr>
          </thead>

          {filterUsers.map((user) => {
            return (
              <tbody key={user.id}>
                <tr>
                  <td>{user.id}</td>
                  <td>{user.firstName}</td>
                  <td>{user.lastName}</td>
                  <td>{user.username}</td>
                </tr>
              </tbody>
            );
          })}
        </table>
      </div>
    );
  };

  const searchUsers = (keyword) => {

    setSearchInput(keyword);

    if(keyword !== ''){
      const results = orders.filter((user) =>{
        return Object.values(user).join('').toLowerCase().includes(keyword.toLowerCase());
      })
      setFilterUsers(results);
    }
    else{
      setFilterUsers(orders);
    }
  }

  return (
    <div className="container mt-5">
      <div>
        <Row>
          <Col>
            <h1>Order List</h1>
          </Col>
          <Col>
            <input icon="search" placeholder="Search by Name..." onChange={(e) => searchUsers(e.target.value)} />
          </Col>
        </Row>
      </div>
      {loading ? <Loading /> : <ShowUsers />}
    </div>
  );
};

export default User;
