import React, { useEffect, useState } from "react";
import {NavLink} from 'react-router-dom';
import axios from "axios";

const Products = () => {
  const [productsData, setProductsData] = useState([]);
  const [filter, setFilter] = useState(productsData);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    axios
      .get("/api/auth/products")
      .then((response) => {
        setProductsData(response.data);
        setLoading(false);
        console.log(response.data);
      })
      .catch((err) => {
        console.log(err);
        setLoading(true);
      });
  }, []);

  const filterProduct = (cat) => {
    const updatedList = productsData.filter((x) => x.category === cat);
    setFilter(updatedList);
}

  const ShowProducts = () => {
    return (
        <>
            <div className="buttons d-flex justify-content-center mb-5 pb-5">
                <button className="btn btn-outline-dark me-2"
                        onClick={() => setFilter(productsData)}>All
                </button>
                <button className="btn btn-outline-secondary me-2"
                        onClick={() => filterProduct("men's clothing")}>
                    Men's Clothing
                </button>
                <button className="btn btn-outline-secondary me-2"
                        onClick={() => filterProduct("women's clothing")}>
                    Women's Clothing
                </button>
                <button className="btn btn-outline-secondary me-2"
                        onClick={() => filterProduct("jewelery")}>Jewelery
                </button>
                <button className="btn btn-outline-secondary me-2"
                        onClick={() => filterProduct("electronics")}>Electronic
                </button>
            </div>
            {filter.map(product => {
                return (
                    <>
                        <div className="col-12 col-md-6 col-lg-4">
                            <div className="card h-100 text-center p-4" key={product.id}>
                                <img
                                    src={product.imgUrl}
                                    className="card-img-top"
                                    alt={product.description}
                                    height="250px"
                                />
                                <div className="card-body">
                                    <h5 className="card-title mb-0">
                                        {product.title.substring(0, 12)}...
                                    </h5>
                                    <p className="card-text lead fw-bold">${product.price}</p>
                                    <NavLink to={`/products/${product.id}`} className="btn btn-outline-dark">
                                        Buy Now
                                    </NavLink>
                                </div>
                            </div>
                        </div>
                    </>
                );
            })}
        </>
    );
};

  return (
    <div>
      <div className="container my-5 py-5">
        <div className="row">
          <div className="col-12 mb-5">
            <h1
              className="display-6 fw-bolder
            text-center"
            >
              Latest Products
            </h1>
            <hr />
          </div>
        </div>
        <div className="row justify-content-center">
          {loading ? <h1>Loading</h1> : <ShowProducts/>}
        </div>
      </div>
    </div>
  );
};

export default Products;
