import axios from "axios";
import React, { useState, useEffect } from "react";
import axiosService from "../service/AxiosService";
import { useLocalState } from "../util/useLocalStroge";

const Cart = () => {
  const value = 0;
  const [jwt, setJwt] = useLocalState("", "jwt");
  const [userData, setUserData] = useState([]);
  const [loading, setLoading] = useState(true);
  const [cart, setCart] = useState([]);

  useEffect(() => {
    axiosService("/cart", jwt)
      .then((response) => {
        setUserData(response.data);
        setCart(response.data.cart.products);
        console.log(response.data.cart.products);
        console.log(response.data);
        setLoading(false);
      })
      .catch((err) => {});
  }, []);

  const Loading = () => {
    return <h1>Loading...</h1>;
  };

  const Cart = () => {
    return (
      <>
        <div className="container mt-5">
          <h4 className="text-end text-muted">{userData.username}</h4>
          {cart.map((items, value) => {
            return (
              <div className="card mt-3 text-center">
                <div className="card-body">
                  <h5 className="card-title mt-3">
                    {userData.cart.products[value].name}
                  </h5>
                  <h6 className="card-subtitle mb-2 mt-3 text-muted">
                    $ {userData.cart.products[value].price}
                  </h6>
                  <div className="text-end">
                    <a href="#" className="card-link">
                      Add
                    </a>
                    <a href="#" className="card-link text-danger">
                      Remove
                    </a>
                  </div>
                </div>
              </div>
            );
          })}

          <div className="mt-4">
            <h4>
              <u>TOTAL</u>
            </h4>
            <h5> $100</h5>
            <div className="text-end"><button className="">Pay</button></div>
          </div>
        </div>

      </>
    );
  };

  const ShowCart = () => {
    return (
      <>
        {cart ? (
          <Cart />
        ) : (
          <div className="container mt-5">There is nothing in this cart</div>
        )}
      </>
    );
  };

  return (
    <div className="container">{loading ? <Loading /> : <ShowCart />}</div>
  );
};

export default Cart;
