import axios from 'axios';
import React, {useState, useEffect} from 'react';
import { useParams } from "react-router-dom";

const OrderProduct = () => {

    const [orderData, setOrderData] = useState([]);
    const { id } = useParams();

    useEffect(() => {
        console.log("Hello World")
        getOrderedProduct();
    }, [])

    const getOrderedProduct = () => {
        axios.get(`/order/get/${id}`)
        .then((response) => {
            setOrderData(response.data);
            console.log(response.data);
        })
    }

    const ShowOrderedProduct = () => {
        return (
            <div>
                        {orde.map((product) => {
          return (
            <div className="col-12 col-md-6 col-lg-4 mt-3" key={product.id}>
              <div className="card h-100 text-center p-4">
                <img
                  src={product.imgUrl}
                  className="card-img-top"
                  alt={product.description}
                  height="250px"
                />
                <div className="card-body">
                  <p className="card-text lead fw-bold mt-2">{product.name}</p>
                  <p className="card-text lead ">${product.price}</p>
                  <NavLink
                    to={`/products/${product.id}`}
                    className="btn btn-outline-dark"
                  >
                    Buy Now
                  </NavLink>
                </div>
              </div>
            </div>
          );
        })}
            </div>
          );
    }

  return (
    <ShowOrderedProduct />
  )
}

export default OrderProduct
