import axios from "axios";
import React, { useState, useEffect } from "react";
import axiosService from "../service/AxiosService";
import { useLocalState } from "../util/useLocalStroge";

const Cart = () => {
  const [jwt, setJwt] = useLocalState("", "jwt");
  const [userData, setUserData] = useState([]);
  const [loading, setLoading] = useState(true);
  const [cart, setCart] = useState([]);

  useEffect(() => {
    axiosService("/cart", jwt)
      .then((response) => {
        setUserData(response.data);
        setCart(response.data.cart.products);
        console.log(response.data.cart.products);
        console.log(response.data);
        setLoading(false);
      })
      .catch((err) => {});
  }, []);

  const Loading = () => {
    return <h1>Loading...</h1>;
  };

  const Cart = () => {
    return (
      <>
        <div className="container mt-5">
          <h4 className="text-end">
            <u>{userData.username}</u>
          </h4>
          {cart.map((items) => {
            return (
              <div className="card mt-3">
                <div className="card-body">
                  <h5 className="card-title">Card title</h5>
                  <h6 className="card-subtitle mb-2 text-muted">
                    Card subtitle
                  </h6>
                  <p className="card-text">
                    Some quick example text to build on the card title and make
                    up the bulk of the card's content.
                  </p>
                  <div className="text-end">
                    <a href="#" className="card-link">
                      Add the Item
                    </a>
                    <a href="#" className="card-link text-danger">
                      Remove the Item
                    </a>
                  </div>
                </div>
              </div>
            );
          })}
        </div>
      </>
    );
  };

  const ShowCart = () => {
    return (
      <>
        {cart ? (
          <Cart />
        ) : (
          <div className="container mt-5">There is nothing in this cart</div>
        )}
      </>
    );
  };

  return (
    <div className="container">{loading ? <Loading /> : <ShowCart />}</div>
  );
};

export default Cart;
