import axios from "axios";
import React, { useState, useEffect } from "react";
import axiosService from "../service/AxiosService";
import { useLocalState } from "../util/useLocalStroge";

const Cart = () => {
  const value = 0;
  const [jwt, setJwt] = useLocalState("", "jwt");
  const [userData, setUserData] = useState([]);
  const [loading, setLoading] = useState(true);
  const [cart, setCart] = useState([]);
  const [item, setItem] = useState(1);

  useEffect(() => {
    axiosService("/cart", jwt)
      .then((response) => {
        setUserData(response.data);
        // setCart(response.data.cart.products);
        // console.log(response.data.cart.products);
        setCart(response.data);
        console.log(response.data);
        setLoading(false);
      })
      .catch((err) => {});
  }, []);

  const Loading = () => {
    return <h1>Loading...</h1>;
  };

  const addItem = (id) => {
    axios.get(`/cart/add/${id}`, {
      headers: {
        "Content-Type": "application/json",
        Authorization: `Bearer ${jwt}`,
      },
    })
    .then(response =>{
      console.log(response.data);
      setItem(item + 1);
      // window.location.href = "/cart";
    })
    .catch(err =>{
      console.log(err);
    });
  }

  const removeItem = (id) => {
    axios.get(`/cart/remove/${id}`, {
      headers: {
        "Content-Type": "application/json",
        Authorization: `Bearer ${jwt}`,
      },
    })
    .then(response =>{
      console.log(response.data);
      window.location.href = "/cart";
    })
    .catch(err =>{
      console.log(err);
    });

    setItem(item - 1);
  }

  const calculatePrice = (price, qry) =>{

  }

  const Cart = () => {
    return (
      <>
        <div className="container mt-5 ">

          {cart.map((product) => {
            return (
              <div className="card mt-3 text-center" key={product.id}>
                <div className="card-body">
                  {/* <h5 className="card-title mt-3">
                    {cart.products[value].name}
                  </h5>
                  <h6 className="card-subtitle mb-2 mt-3 text-muted">
                    ${cart.products[value].price} Ã— {item} = ${cart.products[value].price * item} 
                  </h6>
                  <div className="text-end">
                    <a className="btn btn-info me-2" onClick={() => addItem(cart.products[value].id)}>
                      + Add
                    </a>
                    <a className="btn btn-danger" onClick={() => removeItem(cart.products[value].id)}>
                      - Remove
                    </a>
                  </div> */}
                </div>
              </div>
            );
          })}

          <div className="container mt-4">
            <div className="row">
              <div className="col">
              <h4>
                <u>TOTAL</u>
              </h4>
              <h5> ${0}</h5>
              </div>
              <div className="col text-end mt-3">
                <button type="button" className="btn btn-primary">
                  Payment
                </button>
              </div>
            </div>
          </div>
        </div>
      </>
    );
  };

  const ShowCart = () => {
    return (
      <>
        {cart ? (
          <Cart />
        ) : (
          <div className="container mt-5">There is nothing in this cart</div>
        )}
      </>
    );
  };

  return (
    <div className="container">{loading ? <Loading /> : <ShowCart />}</div>
  );
};

export default Cart;
