import axios from "axios";
import React, { useState, useEffect } from "react";
import { useParams } from "react-router-dom";
import Products from "./Products";

const OrderProduct = () => {
  const [orderData, setOrderData] = useState([]);


const orderedProduct = [
    {
        "id": 1,
        "name": "Coffee",
        "price": 1.25,
        "quantity": 2
    }
]


const result = Object.keys(orderedProduct).map((key) => orderedProduct[key])
  const { id } = useParams();

  useEffect(() => {
    getOrderedProduct();
  }, []);

  const getOrderedProduct = () => {
    axios.get(`/order/get/${id}`).then((response) => {
      setOrderData(response.data);
    //   console.log(response.data.orderedProducts);
    //   setOrderedProduct(response.data.orderedProducts);
    //   console.log(response.data);
      console.log(result)
    });
  };

  const ShowOrderedProduct = () => {
    return (
      <div className="text-center">
        <div className="card">
          <div className="card-body">
            <h6 className="card-subtitle mb-2 text-muted">{orderData.id}</h6>
            <h6 className="card-subtitle mb-2 text-muted">
              {orderData.boughtAt}
            </h6>
            <p className="card-text"></p>
            {
                orderedProduct.map((product) => {
                    return (
                        <div className="container">
                            <h1>{product.name}</h1>
                        </div>
                    )
                })
            }
          </div>
        </div>
      </div>
    );
  };

  return <ShowOrderedProduct />;
};

export default OrderProduct;
