import axios from "axios";
import React, { useState, useEffect } from "react";
import axiosService from "../service/AxiosService";
import { useLocalState } from "../util/useLocalStroge";

const Cart = () => {

  const [jwt, setJwt] = useLocalState("", "jwt");
  const [userData, setUserData] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() =>{
      axiosService("/cart", jwt)
    .then(response =>{
      setUserData(response.data);
      setLoading(false);
    })
    .catch(err =>{
    })
  }, [])

  const Loading = () => {
    return <h1>Loading...</h1>;
  };

  const ShowCart = () => {
    return (
      <>
          {userData.username.cart ? <>There is username</> :
           <div className="container mt-5">There is not</div>}
      </>
    );
  };

  return <div className="container">{loading ? <Loading /> : <ShowCart />}</div>;
};

export default Cart;
